import React from 'react';

const List = props => (

    <ul>
        {props.items.map((item, key) => (

            <li
                key={key}
                className={`${item.completed ? 'completed' : 'pending'}`}>
                {/* if the task is completed we assign
                  the .completed class otherwise .pending
                */}
                {item.task}
                <div className="actions">
                    {/*Using a callback on the onClick we call
                          our markAsCompleted function
                        */}
                    <span className={item.completed ? 'hide' : 'done'}
                        onClick={() => props.markAsCompleted(item.id)}>

                        <i className="fa fa-check"></i>
                    </span>
                    {/*using a callback on the onClick we call
                          our removeTask function*/}
                    <span className="trash"
                        onClick={() => props.removeTask(item.id)}
                    ><i className="fa fa-trash"></i>
                    </span>
                </div>

            </li>
        ))}

    </ul>

);

export default List;
