import React, { Component } from 'react';
//component
import List from '../list/List';
//ID Ramdom
import uuidv4 from 'uuid/v4';
//Style
import './Todo.css'


class Todo extends Component {
    constructor() {
        super();
        this.state = {
            task: '',
            items: []
        };
    }
    componentWillMount() {
        this.setState({
            items: [
                {
                    id: uuidv4(),
                    task: 'pay the rent',
                    completed: false
                },
                {
                    id: uuidv4(),
                    task: 'Go to gym',
                    completed: false
                },
                {
                    id: uuidv4(),
                    task: 'Do my homework',
                    completed: false
                }
            ]
        });
    }
    handleOnChange = e => {
        const { target: { value } } = e;
        this.setState({
            task: value
        })
    }

    handleOnSubmit = e => {
        e.preventDefault();
        if (this.state.task.trim() !== '') {
            this.setState({
                task: '',
                items: [
                    ...this.state.items,
                    {
                        id: uuidv4(),
                        task: this.state.task,
                        completed: false
                    }
                ]
            });

        }
    }

    markAsCompleted = id => {
        //finding the task by id
        const foundTask = this.state.items.find(task => task.id === id);
        //Updating the completed status
        foundTask.completed = true;
        //Updating the state with new state
        this.setState({
            items: [
                ...this.state.items,
                ...foundTask
            ]
        });
    }
    removeTask = id => {
        //Filtering the task by removing the specific task id
        const filteredTask = this.state.items.filter(task => task.id !== id);
        this.setState({
            items: filteredTask
        });
    }
    render() {
        return (
            <div className="Todo">
                <h1>New task:</h1>
                <form onSubmit={this.handleOnSubmit}>
                    <input
                        value={this.state.task}
                        onChange={this.handleOnChange}
                    />
                </form>

                <List
                    items={this.state.items}
                    markAsCompleted={this.markAsCompleted}
                    removeTask={this.removeTask}
                />
            </div>
        )
    }
}
export default Todo;
